function palindrome(str) {
  // Good luck!
  let newarr = [];
  let x = str.replace(/[\s$-/:-?{-~!"^_`\[\]]/g, '').split(/(?=\w)/ig);
  for(let i=x.length-1; i>=0; i--){
    newarr.push(x[i]);
  }
  x = x.join('').toLowerCase(); 
  newarr = newarr.join('').toLowerCase();
